create procedure sp_ssrs_CompetitionCustomersas--exec sp_ssrs_CompetitionCustomersdeclare@RunDb nvarchar(200)set @RunDb = 'MDVALUATE'declare@sql nvarchar(max),@parms nvarchar(max),@CR char(1)set @CR = char(13)set @sql = 'select CustomerName, CustomerID ' + @CRset @sql = @sql + 'from ' + @RunDb + '.dbo.CompetitionMappingMedia ' + @CRset @sql = @sql + 'where IsActive = 1 ' + @CRset @sql = @sql + 'order by CustomerName ' + @CRexec(@sql)